import React from 'react';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import { NavbarComponent } from './components/NavbarComponent';
import { LoginComponent } from './components/default/LoginComponent';
import Welcome from './components/default/Welcome';
import {Logout} from './components/default/Logout';
import Home from './components/default/Home';
import NoMatch from './components/default/NoMatch';
import ControlComponent from './components/controlComponenet';
import EditComponent from './components/editComponenet';
import { Writer } from './model/Writer';
import PostComponent from './components/postComponent';




interface IAppState {
  loggedInWriter: Writer | null;
  id:number|any;
}

export class App extends React.Component<any, IAppState> {

  constructor(props: any) {
    super(props);
    this.state = {
      loggedInWriter: null,
      id: ''
        }
  }

  updateWriter = (user:Writer) => {
    this.setState({
      loggedInWriter: user,
    })
    const cooid = this.state.loggedInWriter?.writerid;
    this.setState({
      id: cooid,
    })
  }

  removeWriter = (u: any) => {
    this.setState({
      loggedInWriter: null,
    })
  }

  render() {
    const G = this.state.loggedInWriter;
    const permission = this.state.loggedInWriter?.permission;
    const writerid = this.state.loggedInWriter?.writerid;
    return (
    <>
    <Router>
      <div className="fixed">
    <Welcome permission={permission} username={G?.firstname || 'Guest'}/>
    <NavbarComponent Writer ={this.state.loggedInWriter} permission={permission}/>
     </div>
     <div className="wrap">
      <Switch>
         <Route path="/" exact>
          <Home />
        </Route> 
      {G && <Route path="/posts" exact>
          <PostComponent />
        </Route>}

        { (permission === 2) && <Route path='/editor' exact >
          <EditComponent userRole={permission} id={this.state.id} xid={writerid}  />
          </Route>}
      { (permission === 1) && <Route path='/control'  exact >
        <ControlComponent userRole={G?.permission} userId={writerid} />
        </Route>}
        { <Route exact path="/login">
          <LoginComponent  updateWriter={this.updateWriter} />
        </Route>}
        { G && <Route exact path="/logout">
          <Logout updateWriter={this.removeWriter}/>
        </Route>}
        <Route><NoMatch updateWriter={this.updateWriter} /></Route>
      </Switch>
      </div>
    </Router>
    </>
    );
  }

}
