import React from 'react';
import { posts, myInfo } from '../api/LibraryClient';
import { Post } from '../models/Post';
import { Postview } from './views/viewPost';
import { NavLink } from 'react-router-dom';

export default class Posts extends React.Component<any,any> {

constructor(props:any){
    super(props);
    this.state = {
        data: [],
        userinfo: [],
        pending: 0,
        resolved: false,
        unresolved:false,
        all:true,
        viewPost: true,
    }
}

async componentDidMount() {
    try {
        this.setState({
            data: await posts(this.props.writerid),
            userinfo: await myInfo(this.props.writerid),


            isError: false,
        })
      } catch (e) {
        // We set the error information in our state.
        // This will let us render something about the error to the end user
        this.setState({
          isError: true,
          errorMessage: e.message,
        })
      // }
    }   
}

resolvedf = () => {
    this.setState({
      resolved: true,
      unresolved: false,
      all:false
    })
  }
  unresolvedf = () => {
    this.setState({
      resolved: false,
      unresolved: true,
      all:false
    })
  }

  allf = () => {
    this.setState({
      resolved: false,
      unresolved: false,
      all:true
    })
  }
    render(){
        const pending = this.state.data.filter((c: { status: string; }) => c.status === "pending").length;
        const resolved = this.state.data.filter((c: { status: string; }) => c.status !== "pending").length;

        return(
            <div className="theFrame">
                <div className="box themain">
        <div className="boxrow spacer">Hi {this.state.userinfo.firstname}</div>  
                <div className="boxrow spacer">&nbsp;</div>  
                <div className="boxrow"><div className="spacer"></div><label className="click" onClick={this.allf}>All posts {pending + resolved}</label> </div>  
                <div className="boxrow"><div className="spacer"></div><label className="click" onClick={this.unresolvedf}>Pending {pending}</label> </div>  
                <div className="boxrow"><div className="spacer"></div><label className="click" onClick={this.resolvedf}>Resolved {resolved}</label> </div> 
                <div className="spcaer">&nbsp;</div>
                <div className="boxrow spacer">

                <NavLink to="/create" id={this.state.userinfo.writerid}><button>create post</button></NavLink></div>  

            </div>
             { this.state.unresolved && (this.state.data.filter((c: { status: string; }) => c.status === "pending").map((u:Post|any,x:number) =>{
                return(
                    <Postview key={x} thekey={x} id={u.postid} author={u.author} postTitle={u.postTitle}
                    postDescription={u.postDescription} postText={u.postText} keywords={u.keyword} status={u.status}
                    resolver={u.resolver} dateSubmitted={u.dateSubmitted} />
                    )      
                    })         
        )}
        {this.state.resolved && (this.state.data.filter((c: { status: string; }) => c.status !== "pending").map((u:Post|any,x:number) =>{
                return(
                    <Postview key={x} thekey={x} id={u.postid} author={u.author} postTitle={u.postTitle}
                    postDescription={u.postDescription} status={u.status}
                    resolver={u.resolver} dateSubmitted={u.dateSubmitted} />
                    )      
                    })         
        )}
        {this.state.all && (this.state.data.map((u:Post|any,x:number) =>{
                return(
                    <Postview key={x} thekey={x} id={u.postid} author={u.author} postTitle={u.postTitle}
                    postDescription={u.postDescription} status={u.status}
                    resolver={u.resolver} dateSubmitted={u.dateSubmitted} />
                    )      
                    })         
        )}

        </div>
        )
    }
}
