import React from 'react';
import { User } from '../models/User';
import { getSingle } from '../api/LibraryClient';
// import { QuickTable } from './QuickTable';

interface IBooksDisplayState {
  Onemembers: User[]|any;
  isError: boolean;
  errorMessage: string;
  userid : number
}

export class SingleUser extends React.Component<any, IBooksDisplayState> {

  constructor(props: any) {
    super(props);
    this.state = {
        Onemembers: '',
      isError: false,
      errorMessage: '',
      userid: 1
    }
  }

  async componentDidMount() {
    try {
      this.setState({
        Onemembers: await getSingle(this.state.userid)
      })
    } catch (e) {

      this.setState({
        isError: true,
        errorMessage: e.message,
      })
    }
  }
  shouldComponentUpdate(nextProps: any, nextState: any) {
    // If this returns true, the component will update.  If false, the component won't.
    // should update if we have different response from SWAPI, otherwise no.
    return this.state.Onemembers !== nextState.Onemembers;
  }

  async componentDidUpdate() {
    //This runs when the component updates on the actual DOM
    this.setState({
      Onemembers: await getSingle(this.state.userid)
    });
  }

  incrementPlanetId = () => {
    this.setState({
      userid: this.state.userid + 1
    })
  }

  clearError = () => {
    this.setState({
      isError: false,
      errorMessage: '',
    });
  }

  render() {
    return (
      <>
      {/* Button that adds one to planetId */}
      <form>
        <input type="text" placeholder=" please type user id" />
      <button onClick={this.incrementPlanetId}>Next user</button>
      </form>

      {/* We needed the JSON.stringify to turn our planet object into a string */}
      <p>Response from SWAPI for id {this.state.userid}: {JSON.stringify(this.state.Onemembers)}</p>
      </>
    );


