import React from 'react';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import { NavbarComponent } from './components/NavbarComponent';
import { User } from './models/User';
import { LoginComponent } from './components/LoginComponent';
import Welcome from './components/Welcome';
import {Logout} from './components/Logout';
import {getUser} from './components/getUser';
import {SingleUser} from './components/SingleUser';
import Home from './components/Home'



interface IAppState {
  loggedInUser: User | null;
  // members: User | null;
}

export class App extends React.Component<any, IAppState> {

  constructor(props: any) {
    super(props);
    this.state = {
      loggedInUser: null,
      // members :null,
    }
  }

  updateUser = (user:User) => {
    this.setState({
      loggedInUser: user,
    })
  }

  // emptyMembers = (m:any) => {
  //   this.setState({
  //     members: null,
  //   })
  // }

  removeUser = (u: any) => {
    this.setState({
      loggedInUser: null,
    })
  }

  render() {
    const G = this.state.loggedInUser;
    return (
    <>
    <Router>
      <div className="fixed">
    <Welcome role={G?.role || 'visitor'} username={G?.firstname || 'Guest'}/>
    <NavbarComponent User = {this.state.loggedInUser}/>
     </div>
     <div className="wrap">
      <Switch>
      <Route path="/hello">
          <Home />
        </Route> 
       <Route path="/login">
          <LoginComponent updateUser={this.updateUser} />
        </Route>
        { G && <Route path="/logout">
          <Logout updateUser={this.removeUser}/>
        </Route>}
      { G && <Route path='/users' exact component={getUser} />}
      { G && <Route path='/users/1' exact component={SingleUser} />
        }
      </Switch>
      </div>
    </Router>
    </>
    );
  }

}
