import React from 'react';

// This component takes props.

// Props are the data we pass into a component.  props are immutable, so we cant change
// the props isnide the component.  We specify props using an interface in TS

interface IMessageProps {
  message: string;
}

// React.Component has two generics, one from props and one for state.  For now we'll
// jsut use the first.
export class Message extends React.Component<IMessageProps> {

  render() {
    // curly braces in JSX let us write JS that is evaluated, should be an expression
    return (
    <p>Here is the message: {this.props.message}</p>
    );
  }

}
