import React from 'react';
import { User } from '../models/User';
import { updatexx } from '../api/LibraryClient';
import { Route, Switch, Redirect} from 'react-router';

//the updateUser prop takes a function that takes a user and returns voide
// it will match updateUser in App.
interface IUpdateComponentProps {
  updateUserxx: (user:User) => void;
}

interface ILoginComponentState {
  username: string;
  password: string;
  firstname: string;
  lastname: string;
  email: string;
  role: string;
  userid: number | any;
  isError: boolean;
  errorMessage: string;
  success:boolean;
}

export class UpdateUsers extends React.Component<IUpdateComponentProps, ILoginComponentState> {

  constructor(props: IUpdateComponentProps) {
    super(props);
    this.state = {
      username: '',
      password: '',
      firstname: '',
      lastname: '',
      email: '',
      role: '',
      userid: 0,
      isError: false,
      errorMessage: '',
      success:false
    }
  }

  //We'll need a few functions to modify individual pieces of our state
  // These take change events
  setUsername = (un: any) => {
    this.setState({
      username: un.currentTarget.value,
    })
  }

  setPassword = (pw: any) => {
    this.setState({
      password: pw.currentTarget.value,
    })
  }
  setFirstname = (pw: any) => {
    this.setState({
      firstname: pw.currentTarget.value,
    })
  }
  setLastname= (pw: any) => {
    this.setState({
      lastname: pw.currentTarget.value,
    })
  }
  setEmail = (pw: any) => {
    this.setState({
      email: pw.currentTarget.value,
    })
  }
  setRoleid= (pw: any) => {
    this.setState({
      role: pw.currentTarget.value,
    })
  }
  setUserid = (pw: any) => {
    this.setState({
      userid: pw.currentTarget.value,
    })
  }

  clearError = () => {
    this.setState({
      errorMessage: '',
      isError: false,
      success: false
    })
  }

  attemptUpdate = async (event: any) => {
    event.preventDefault();
    console.log(event);
    try {
      const levelup : User = await updatexx(this.state.username, this.state.password,this.state.firstname,this.state.lastname,this.state.email,this.state.role,this.state.userid);
      this.props.updateUserxx(levelup);
      this.setState({
        username: '',
        password: '',
        success: true
      });
      console.log(levelup);

    } catch (error) {
      this.setState({
        password: '',
        isError: true,
        success:false,
        errorMessage: error.message,
      })
    }
  }

  render() {
    if(!this.state.success){
    return (
      <div>
      <form onSubmit={this.attemptUpdate}>
        <div>
          <label>Username</label>
          <input onChange={this.setUsername} value={this.state.username} type="text" name="username"
           id="username" placeholder="your username"
           autoComplete={this.state.username} required/>
          </div>
          <div>
          <label>ID</label>
            <input onChange={this.setUserid} value={this.state.password} type="password" name="password" id="password" 
            placeholder="ID" autoComplete={this.state.userid} required />
          </div>
          <div>
          <label>firstname</label>
            <input onChange={this.setFirstname} value={this.state.firstname} type="password" name="password" id="password" 
            placeholder="password" autoComplete={this.state.password} required />
          </div>
          <div>
          <label>Password</label>
            <input onChange={this.setPassword} value={this.state.password} type="password" name="password" id="password" 
            placeholder="password" autoComplete={this.state.password} required />
          </div>
          <div>
          <label>firstname</label>
            <input onChange={this.setFirstname} value={this.state.firstname} type="password" name="password" id="password" 
            placeholder="First name" autoComplete={this.state.firstname} required />
          </div>
          <div>
          <label>lastname</label>
            <input onChange={this.setLastname} value={this.state.firstname} type="password" name="password" id="password" 
            placeholder="Last name" autoComplete={this.state.lastname} required />
          </div>
          <div>
          <label>email</label>
            <input onChange={this.setEmail} value={this.state.email} type="password" name="password" id="password" 
            placeholder="Email" autoComplete={this.state.email} required />
          </div>
          <div>
          <label>role</label>
            <input onChange={this.setEmail} value={this.state.role} type="password" name="password" id="password" 
            placeholder="Role" autoComplete={this.state.role} required />
          </div>

          <div className="notdiv">
            <input type="submit" value="submit" />
          </div>
      </form>
     {this.state.isError && <h3>{this.state.errorMessage}</h3>}
      </div>
    );
    }
    else{ return (
      <>
    <h3>user updated</h3>
    {/* <Switch>
      <Route>
      <Redirect to="/hello"/>
      </Route>
    </Switch> */}
    </>
     ) 
    }
  }

}
