import React from 'react';
import { User } from '../models/User';
import {Toast, ToastHeader, ToastBody } from 'reactstrap';
import { getAllUsers } from '../api/LibraryClient';
// import { Redirect } from 'react-router';

//the updateUser prop takes a function that takes a user and returns voide
// it will match updateUser in App.
interface ILoginComponentProps {
  updateUsers: (user:User) => void;
}

interface ILoginComponentState {
  members : any,
  allUsers :any
}

export class LoginComponent extends React.Component<ILoginComponentProps, ILoginComponentState> {

  constructor(props: ILoginComponentProps) {
    super(props);
    this.state = {
      members: [],
      allUsers: '',
    }
  }

  attemptFetch = async (event: any) => {
    event.preventDefault();
    console.log(event);
    try {
      const allUsers : any = await getAllUsers();
      this.props.updateUsers(allUsers);


    } catch (error) {
     console.log(error);
    }
  }

  render() {
    return (
      <>
      {this.state.allUsers.map((user: any)=>{
          <span>user.username</span>
      })}
      </>
    );
  }

}
