import React from 'react';
import { Writer } from '../../models/Writer';
import { updatexx } from '../../api/LibraryClient';
import { Eview } from './view';
import { Err1 } from '../../errors/error1';
import  bcrypt from 'bcryptjs';

// import { Route, Switch, Redirect} from 'react-router';

//the updateWriter prop takes a function that takes a user and returns voide
// it will match updateWriter in App.
interface IUpdateComponentProps {
  updateWriterxx: (user:Writer) => void;
}

interface ILoginComponentState {
  username: string|any;
  password: string|any;
  firstname: string|any;
  lastname: string|any;
  email: string|any;
  permission: number|any;
  phone: string|any;
  userid: number | any;
  isError: boolean;
  errorMessage: string;
  success:boolean;
  user: Writer |any;
}

export class UpdateWriters extends React.Component<IUpdateComponentProps, ILoginComponentState> {

  constructor(props: IUpdateComponentProps) {
    super(props);
    this.state = {
      username: '',
      password: '',
      firstname: '',
      lastname: '',
      email: '',
      phone: '',
      permission: '',
      userid: '',
      isError: false,
      errorMessage: '',
      success:false,
      user:'',
    }
  }

  //We'll need a few functions to modify individual pieces of our state
  // These take change events
  setWritername = (un: any) => {   
    this.setState({
        username: un.currentTarget.value
        })  
  }

  setPassword = (pw: any) => {
    this.setState({
      password: pw.currentTarget.value,
    })
  }
  setFirstname = (pw: any) => {
    this.setState({
      firstname: pw.currentTarget.value,
    })
  }
  setLastname= (pw: any) => {
    this.setState({
      lastname: pw.currentTarget.value,
    })
  }
  setEmail = (pw: any) => {
    this.setState({
      email: pw.currentTarget.value,
    })
  }
  setPhone = (pw: any) => {
    this.setState({
      phone: pw.currentTarget.value,
    })
  }
  setPermission= (pw: any) => {
    this.setState({
      permission: pw.currentTarget.value,
    })
  }
  setWriterid = (pw: any) => {
    this.setState({
      userid: pw.currentTarget.value,
    })
  }

  clearError = () => {
    this.setState({
      errorMessage: '',
      isError: false,
      success: false,
      username: '',
      password: '',
      firstname: '',
      lastname: '',
      email: '',
      permission: '',

    })
  }

  attemptUpdate = async (event: any) => {
    event.preventDefault();
    console.log(event);
    try {
      const levelup : Writer = await writerUpdate(this.state.userid,this.state.username,this.state.password,this.state.firstname,this.state.lastname,this.state.phone,this.state.email,this.state.permission);
      this.props.updateWriterxx(levelup);
      this.setState({
          user: levelup,
        username: '',
        password: '',
        success: true
      });
      console.log(levelup);

    } catch (error) {
      this.setState({
        password: '',
        isError: true,
        success:false,
        errorMessage: error.message,
      })
    }
  }

  render() {
      if(this.state.isError){
          return <Err1 err={this.state.errorMessage} />
      }
    if(!this.state.success){
    return (
      <div>
      <form onSubmit={this.attemptUpdate}>
        <span className="tag"> update</span>
          <div>
            <input onChange={this.setWriterid} value={this.state.userid} type="number" name="writerid"  
            placeholder="ID (required!)"  required />
          </div>
          <div>
          <input onChange={this.setWritername} value={this.state.username} type="text" name="username"
           id="username" placeholder="change username"
           autoComplete={this.state.username} />
            <input onChange={this.setPassword} value={this.state.password} type="password" name="password" 
            placeholder="change password" autoComplete='false' />
          </div>
          <div>
            <input onChange={this.setFirstname} value={this.state.firstname} type="text" name="firstname" 
            placeholder="change firstname"  />
            <input onChange={this.setLastname} value={this.state.lastname} type="text" name="lastname" 
            placeholder="Last name" />
          </div>
          <div>
            <input onChange={this.setEmail} value={this.state.email} type="email" name="email" 
            placeholder="change email"  />
            <input onChange={this.setPhone} value={this.state.email} type="tel" name="phone" 
            placeholder="change phone"  />
            <select onChange={this.setPermission} name="permission">
            <option value="">select permission</option>
            <option value="1">Admin</option>
            <option value="2">Editor</option>
            <option value="3">Writer</option>
            </select>
          </div>

          <div className="notdiv">
            <input type="submit" value="submit" />
          </div>
      </form>
     {<h3>{this.state.errorMessage}</h3>}
      </div>
    );
    }
    else{ 

        const data = this.state.user;

        return (
      <>
    <h3>user updated successfuly
        <span className="button" onClick={this.clearError}>Make another update</span>
    </h3>
    <Eview access={data.id} id={data.id} username={data.username} pass={data.password}
          firstname={data.firstname} lastname={data.lastname}
           email={data.email} role={data.role} />
    </>
     ) 
    }
  }

}
