import React from 'react';
import { getSingle } from '../../api/LibraryClient';
import { ErrS, Err1 } from '../../errors/error1';


interface IBooksDisplayState {
  single: any;
  isError: boolean;
  errorMessage: string;
}

export class SingleUser extends React.Component<any, IBooksDisplayState> {

  constructor(props: any) {
    super(props);
    this.state = {
      single: 'fetching user by id',
      isError: false,
      errorMessage: '',
    }
  }

  async componentDidMount() {
    try {
      this.setState({
        single: await getSingle(this.props.userid),
        isError: false
      })
    } catch (e) {
      // We set the error information in our state.
      // This will let us render something about the error to the end user
      this.setState({
        single: '',
        isError: true,
        errorMessage: e.message,
      })
    // }
  }
  }
  shouldComponentUpdate(nextProps: any, nextState: any) {
    return this.state.single !== nextState.single;
  }

  async componentDidUpdate() {
    //This runs when the component updates on the actual DOM
    this.setState({
      single: await getSingle(this.props.userid),
    });
  }



  clearError = () => {
    this.setState({
      isError: false,
      errorMessage: '',
    });
  }



  render() {
    const data = this.state.single;

if(this.state.isError){
  return <Err1 err={this.state.errorMessage} />

    }else{
    return(
      <>

      <div key={data.id} className="col">
<span>userid: <span>{data.id}</span> </span>  
<span>username:  {data.username}</span>  
<span>password:  {data.password}</span>  
<span>firstname: {data.firstname}</span>  
<span>lastname: {data.lastname}</span>  
<span>email: {data.email}</span>  
<span>role: {data.role}</span> 
<span>{(typeof(data) === "string") && <div className="dark">"No such employee with provided ID"</div>} </span> 
</div>


</>

    )
  }

  }

}
