import React from 'react';
import { getPostById } from '../../api/LibraryClient';
import { FormGroup, Input, Form } from 'reactstrap';


export  class Postview extends React.Component<any,any> {
    constructor(props:any){
        super(props);
        this.state= {
            viewPost:false,
            editPost:false,
            postValue: this.props.postid,
            data:[],
            text:'',
        description: '',
        type: '',
        field: '',
        title: '',
        keys: '',
        }

    }
    async getPost(){
        this.setState({
            data:  await getPostById(1),
        });
  }

  setText= (pw: any) => {
    this.setState({
      text: pw.currentTarget.value,
    })
  }
  setDescription = (pw: any) => {
    this.setState({
        description: pw.currentTarget.value,
    })
  }
  setType= (pw: any) => {
    this.setState({
      type: pw.currentTarget.value,
    })
  }
  setField= (pw: any) => {
    this.setState({
      field: pw.currentTarget.value,
    })
  }
  setTitle= (pw: any) => {
    this.setState({
      title: pw.currentTarget.value,
    })
  }

  setKeys= (pw: any) => {
    this.setState({
      keys: pw.currentTarget.value,
    })
  }
    disable = () => {
        this.setState({
          viewPost: false
        })
      }
       enable = () =>{
            this.setState({
                data:  getPostById(this.state.postValue),
                viewPost: true
            });

      }
      disableE = () => {
        this.setState({
          editPost: false
        })
      }
       enableE = () =>{
            this.setState({
                data:  getPostById(this.state.postValue),
                editPost: true
            });

      }
    render(){
        return(
            <>
            <div key={Math.floor(Math.random() * 10)} className="box">
            <div className="boxrow">created:  {this.props.dateSubmitted}</div>  
            <div className="boxrow"><span>{this.props.status}</span></div>  
            <div className="boxrow"><h1>{this.props.postTitle}</h1></div>  
            <div className="spacer">&nbsp;</div>
            <div className="boxrow">
                <button onClick={this.enable}>Preview</button>
                <button onClick={this.enableE}>Edit</button></div>  
          </div>
          {this.state.viewPost && <div className="overlay">
            <span className="close" onClick={this.disable}>&times;</span>
            <div className="viewHolder">
        <h1>{this.props.postTitle}</h1>
        <p className="desc">{this.props.postDescription}</p>
        <p className="body">{this.props.postText}</p>
        <p className="keys"> {this.props.postType} / &nbsp;  {this.props.postField}: <br/>keywords: {this.props.keywords}</p>
            </div>
        </div>}
        {this.state.editPost && <div className="overlay">
            <span className="close" onClick={this.disableE}>&times;</span>
            <div className="viewHolder">
            <Form onSubmit={this.attemptUpdate}>
        <span className="tag"> new post</span>
            <FormGroup>
          <Input onChange={this.setTitle} value={this.state.title} type="text" name="title"
           id="posttitle" placeholder="set title"
           required/>
           </FormGroup>
           <FormGroup>
           <Input onChange={this.setDescription} value={this.state.description} type="text" name="description"
           id="description" placeholder="set description"
           required/>
           </FormGroup>
           <FormGroup>
           <Input onChange={this.setKeys} value={this.state.keys} type="text" name="keywrods"
           id="keywords" placeholder="set key words"
           required/>
           </FormGroup>
           <FormGroup>
            <textarea name="text" onChange={this.setText}>&nbsp;</textarea>

          </FormGroup>

            <FormGroup>
            <select onChange={this.setField} name="filed" required>
            <option >select type</option>
            <option value="Politics">Politics</option>
            <option value="Sport">Sport</option>
            <option value="Economics">Economics</option>
            </select>
            </FormGroup><FormGroup>
            <select onChange={this.setType} name="type" required>
            <option >select Field</option>
            <option value="Article">Article</option>
            <option value="News">News</option>
            <option value="Story">Story</option>
            </select>
          </FormGroup>

          <FormGroup className="notdiv">
            <Input type="submit" value="submit" />
          </FormGroup>
      </Form>
         </div>
        </div>}
            </>
        )
    }
}
