import React from 'react';
import { Writer } from '../model/Writer';






// interface IAppState {
//     userid:number;
//     single: any;
//     levelup: Writer[] | any;
//     userRole: any;
//     userId: any;
//   }

export default class WriterComponent extends React.Component<any,any> {



    render(){
        return(
            <>
            { (this.props.userRole === 1) &&
            <div className="panel" id="admin page">
            <span> profile </span>
            <span> posts </span>
            <span> control </span>
            </div>}
            { (this.props.userRole === 2) &&
            <div className="panel" id="admin page">
            <span> profile </span>
            <span> posts </span>
            <span> editor </span>
            </div>}

            { (this.props.userRole === 3) &&
            <div className="panel" id="admin page">
            <span>Profile </span>
            <span> posts  </span>
            </div>}

          { this.state.random &&   <div className="dif">
                <form onSubmit={this.handleSubmit} className="narrow">
                <label>Employee ID: </label>
                <input type="text" onChange={this.handleChange} />
                <input type="submit" onClick={this.handleSubmit} value="Fetch" />
                <label>&nbsp;</label>
                </form>
            </div>}
            <div className="wrapmein">

            { this.state.random && <SingleWriter writerid={this.state.value} good={this.state.good}/>}
            { this.state.random1 && <AllWriters />}
            { this.state.random2 && <UpdateWriters updateWriterxx={this.updateWriterxx}/>}

            { this.state.randomx1 && <AllWritersx getWriter={this.state.writerId} />}
            { this.state.randomx2 && <UpdateWritersx getWriter={this.state.writerId} updateWriterxx={this.updateWriterxx}/>}
               </div>          
            </>

        )
    }
}
