// This doesn't strictly need to be called App, its just the top level component.
import React from 'react';
import { HelloWorld } from './components/HelloWorld';
// import { Message } from './components/Message';
// import { ClickerCounter } from './components/ClickerCounter';
// import { QuickTable } from './components/QuickTable';
// import { NumberTableBuilder } from './components/NumberTableBuilder';
import { SwapiQuery } from './components/SwapiQuery';
// import  {BooksDisplay} from './components/BooksDisplay';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
// import { MultiRoute } from './components/MultiRoute';
import { NavbarComponent } from './components/NavbarComponent';
// import { Example } from './components/NavReactstrap'
import { User } from './models/User';
import { LoginComponent } from './components/LoginComponent';
import Welcome from './components/Welcome';
import {Logout} from './components/Logout';
import {getUser} from './components/getUser';


interface IAppState {
  loggedInUser: User | null;
}

export class App extends React.Component<any, IAppState> {

  constructor(props: any) {
    super(props);
    this.state = {
      loggedInUser: null,
    }
  }

  updateUser = (user:User) => {
    this.setState({
      loggedInUser: user,
    })
  }

  removeUser = (u: any) => {
    this.setState({
      loggedInUser: null,
    })
  }

  render() {
    return (
    <>
    <Router>
      <div className="fixed">
    <Welcome role={this.state.loggedInUser?.role || 'visitor'} username={this.state.loggedInUser?.firstname || 'Guest'}/>
    <NavbarComponent User = {this.state.loggedInUser}/>
     </div>
     <div className="fixed">
      <Switch>   
      { !this.state.loggedInUser && <Route path="/login">
          <LoginComponent updateUser={this.updateUser} />
        </Route>}
        { this.state.loggedInUser && <Route path="/logout">
          <Logout updateUser={this.updateUser} />
        </Route>}
      <Route path='/users' component={getUser} />
      </Switch>
      </div>
    </Router>
    </>
    );
  }

}
