import React from 'react';
import { posts } from '../api/LibraryClient';
import { Post } from '../models/Post';
import { Postview } from './views/viewPost';

export default class Posts extends React.Component<any,any> {

constructor(props:any){
    super(props);
    this.state = {
        single: [],
        user:[]

    }
}

async componentDidMount() {
    try {
        this.setState({
            single: await posts(),
            single: await myInfo(this.props.postid),
            isError: false,
        })
      } catch (e) {
        // We set the error information in our state.
        // This will let us render something about the error to the end user
        this.setState({
          isError: true,
          errorMessage: e.message,
        })
      // }
    }   
}
    render(){
        return(
            <div className="theFrame">
             {(this.state.single.map((u:Post|any,x:number) =>{
                return(
                    <Postview key={x} thekey={x} id={u.postid} author={u.author} postTitle={u.postTitle}
                    postDescription={u.postDescription} status={u.status}
                    resolver={u.resolver} dateSubmitted={u.dateSubmitted} />

                    )      
                    })

        )}
        </div>
        )
    }
}
