import React from 'react';
import { getPostById } from '../../api/LibraryClient';


export  class Postview extends React.Component<any,any> {
    constructor(props:any){
        super(props);
        this.state= {
            viewPost:false,
            postValue: this.props.postid,
            data:[],
        }

    }
    async getPost(){
        this.setState({
            data:  await getPostById(1),
        });
  }

    disable = () => {
        this.setState({
          viewPost: false
        })
      }
       enable = () =>{
            this.setState({
                data:  getPostById(this.state.postValue),
                viewPost: true
            });

      }
    render(){
        return(
            <>
            <div key={Math.floor(Math.random() * 10)} className="box">
            <div className="boxrow">created:  {this.props.dateSubmitted}</div>  
            <div className="boxrow"><span>{this.props.status}</span></div>  
            <div className="boxrow"><h1>{this.props.postTitle}</h1></div>  
            <div className="spacer">&nbsp;</div>
            <div className="boxrow">
                <button onClick={this.enable}>Preview</button>
                <button onClick={this.enableE}>Edit</button></div>  
          </div>
          {this.state.viewPost && <div className="overlay">
            <span className="close" onClick={this.disable}>&times;</span>
            <div className="viewHolder">
        <h1>{this.props.postTitle}</h1>
        <p className="desc">{this.props.postDescription}</p>
        <p className="body">{this.props.postText}</p>
        <p className="keys"> {this.props.postType} / &nbsp;  {this.props.postField}: <br/>keywords: {this.props.keywords}</p>
            </div>
        </div>}
        {this.state.editPost && <div className="overlay">
            <span className="close" onClick={this.disableE}>&times;</span>
            <div className="viewHolder">
        <h1>{this.props.postTitle}</h1>
        <p className="desc">{this.props.postDescription}</p>
        <p className="body">{this.props.postText}</p>
        <p className="keys"> {this.props.postType} / &nbsp;  {this.props.postField}: <br/>keywords: {this.props.keywords}</p>
            </div>
        </div>}
            </>
        )
    }
}
