import React from 'react';
import { User } from '../models/User';
import { getAllUsers } from '../api/LibraryClient';
// import { QuickTable } from './QuickTable';

interface IBooksDisplayState {
  members: User[];
  isError: boolean;
  errorMessage: string;
}

export class getUser extends React.Component<any, IBooksDisplayState> {

  constructor(props: any) {
    super(props);
    this.state = {
      members: [],
      isError: false,
      errorMessage: '',
    }
  }

  async componentDidMount() {
    try {
      this.setState({
        members: await getAllUsers()
      })
    } catch (e) {
      // We set the error information in our state.
      // This will let us render something about the error to the end user
      this.setState({

        isError: true,
        errorMessage: e.message,
      })
    }
  }



  clearError = () => {
    this.setState({
      isError: false,
      errorMessage: '',
    });
  }

  render() {

    if(!this.state.isError) {
      //map to a string so quicktable can render this.

      return this.state.members.map((u:User)=>{
     return(
          <div key={u.id} className="col">
          <span>userid:  {u.id}</span>  
          <span>username:  {u.username}</span>  
          <span>password:  {u.password}</span>  
          <span>firstname: {u.firstname}</span>  
          <span>lastname: {u.lastname}</span>  
          <span>email: {u.email}</span>  
          <span>role: {u.role}</span>  
          </div>
         )      
         })
    } 
else {

            return  <h3>Finance manager role is required!</h3>

    }

  }

}
