import React from 'react';
import { User } from '../models/User';
import {Toast, ToastHeader, ToastBody } from 'reactstrap';
import { login } from '../api/LibraryClient';
import { Redirect } from 'react-router-dom';

//the updateUser prop takes a function that takes a user and returns voide
// it will match updateUser in App.
interface ILoginComponentProps {
  updateUser: (user:User) => void;
}

interface ILoginComponentState {
  username: string;
  password: string;
  isError: boolean;
  errorMessage: string;
}

export class LoginComponent extends React.Component<ILoginComponentProps, ILoginComponentState> {

  constructor(props: ILoginComponentProps) {
    super(props);
    this.state = {
      username: '',
      password: '',
      isError: false,
      errorMessage: '',
    }
  }

  //We'll need a few functions to modify individual pieces of our state
  // These take change events
  setUsername = (un: any) => {
    this.setState({
      username: un.currentTarget.value,
    })
  }

  setPassword = (pw: any) => {
    this.setState({
      password: pw.currentTarget.value,
    })
  }

  clearError = () => {
    this.setState({
      errorMessage: '',
      isError: false,
    })
  }

  attemptLogin = async (event: any) => {
    event.preventDefault();
    console.log(event);
    try {
      const loggedInUser : User = await login(this.state.username, this.state.password);
      this.props.updateUser(loggedInUser);
      this.setState({
        username: '',
        password: '',
      });
    if(loggedInUser){ return <Redirect to="/hello" />};
    } catch (error) {
      this.setState({
        password: '',
        isError: true,
        errorMessage: error.message,
      })
    }
  }

  render() {
    return (
      <div>
      <form onSubmit={this.attemptLogin}>
        <div>
          <label>Username</label>
          <input onChange={this.setUsername} value={this.state.username} type="text" name="username" id="username" placeholder="your username" required/>
          </div>
          <div>
          <label>Password</label>
            <input onChange={this.setPassword} value={this.state.password} type="password" name="password" id="password" placeholder="password" required />
          </div>
          <div className="notdiv">
            <input type="submit" value="submit" />
          </div>
      </form>
      <Toast isOpen={this.state.isError}>
        <ToastHeader icon="danger" toggle={this.clearError}>
          Error!
        </ToastHeader>
        <ToastBody>
          {this.state.errorMessage}
        </ToastBody>
      </Toast>
      </div>
    );
  }

}
