import React from 'react';
import { posts, myInfo } from '../api/LibraryClient';
import { Post } from '../models/Post';
import { Postview } from './views/viewPost';

export default class Posts extends React.Component<any,any> {

constructor(props:any){
    super(props);
    this.state = {
        data: [],
        userinfo:[],
        pending: 0,



    }
}

async componentDidMount() {
    try {
        this.setState({
            data: await posts(this.props.writerid),
            userinfo: myInfo(this.props.writerid),

            isError: false,
        })
      } catch (e) {
        // We set the error information in our state.
        // This will let us render something about the error to the end user
        this.setState({
          isError: true,
          errorMessage: e.message,
        })
      // }
    }   
}
    render(){
        const pending = this.state.data.filter((c: { status: string; }) => c.status === "pending").length;

        return(
            <div className="theFrame">
                <div className="box">
        <div className="boxrow">Hi {pending} </div>  
            </div>
             {(this.state.data.map((u:Post|any,x:number) =>{
                return(
                    <Postview key={x} thekey={x} id={u.postid} author={u.author} postTitle={u.postTitle}
                    postDescription={u.postDescription} status={u.status}
                    resolver={u.resolver} dateSubmitted={u.dateSubmitted} />
                    )      
                    })

        )}
        </div>
        )
    }
}
