import React from 'react';
import { openInbox } from '../../api/LibraryClient';
import { User } from '../../models/User';
import { Message } from '../../models/Message';
import { Inbox } from '../../models/Inbox';
import { ListGroup, ListGroupItem } from 'reactstrap';

interface IInboxPageProps {
    owner : User;
}

interface IInboxPageState {
    messages : Message[];
}

export class InboxPage extends React.Component<IInboxPageProps, IInboxPageState> {

    constructor(props : IInboxPageProps) {
        super(props);
        this.state = {
            messages: []
        }
    }

    async componentDidMount() {
        const myInbox : Inbox = await openInbox(this.props.owner.writerid);
        this.setState({
            messages: myInbox.messages
        });
    }

    render() {
        const testMsg = this.state.messages[0];
        const bestmsg = this.state.messages[1];
        return (
            <div className="wrap">
                <div className="usercontainer">
                    <div className="rowss">
                        <div>
                            <span className="profile">Reply</span>
                            <span>{testMsg ? testMsg.sender.firstname + ' ' + testMsg.sender.lastname : null}</span>
                            <span></span>
                            <span className="id">{testMsg ? testMsg.messageStatus : null}</span>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
