import React from 'react';
import {AllUsers} from './UsersAll';
import {SingleUser} from './SingleUser';
// import { User } from '../models/User';
import { getSingle } from '../api/LibraryClient';
import { User } from '../models/User copy';



interface IAppState {
    userid:number;
    single: any;
  }

export default class users extends React.Component<IAppState,any> {

constructor(props:any){
    super(props);
    this.state = {
        userid : 2,
        value: '',
        single:'' ,
        input: '',
        handleChange : this.handleChange.bind(this),
        handleSubmit : this.handleSubmit.bind(this),
        random: false,
        random1: false
    }
}
// shouldComponentUpdate(nextProps: any, nextState: any) {
//     return this.state.value !== nextState.value;
//   }

showIn = () => {
    this.setState({
      random: true,
      random1: false
    })
  }

  showAll = () => {
    this.setState({
      random: false ,
      random1: true   
    })
  }

changevalue = () => {
    this.setState({
      value:  1     
    })
  }

  handleChange = (e:any) => {
    this.setState({input: e.target.value});
    this.setState({random : true})
  }

  handleSubmit = (event:any)=> {
    this.setState({value: this.state.input});
    event.preventDefault();
  }

    render(){
        return(
            <>
            <div className="panel">
            <span  onClick={this.showIn}>Individual </span>
            <span  onClick={this.showAll}>All </span>
            </div>

            <div className="dif">
                <form onSubmit={this.handleSubmit}>
                <label>Employee ID: </label>
                <input type="text" onChange={this.handleChange} />
                <input type="submit" value="Fetch" />
                </form>
            </div>


            <div id ="hideme" className="wrapmein">
            { this.state.random && <SingleUser userid={this.state.value}/>}
            { this.state.random1 && <AllUsers />}
               </div>          
            </>

        )
    }
}
