import React from 'react';
import { patching } from '../../api/LibraryClient';

interface cc {
    ide:number;
    res:number;
    status:string;
}
export  class Review extends React.Component<any,cc> {
    constructor(props:any){
        super(props);
        this.deny = this.deny.bind(this);
        this.approve = this.approve.bind(this);

        this.state ={
            ide: this.props.id,
            res: this.props.resolverr,
            status : this.props.status,
        }


    }

    shouldComponentUpdate(nextProps: any, nextState: any) {
        return this.state.status !== nextState.status;
      }
    deny = (x:any) =>{
        patching(this.props.id,this.props.resolverr,null,null,3,null); 
        this.forceUpdate();
        this.setState({
            status: 'deny'  ,  
        })


    }
    approve = (x:any) =>{
         patching(this.props.id,this.props.resolverr,null,null,2,null);
         this.forceUpdate();
         this.setState({
            status: 'approved'  ,  
        })
    }

    pending = (x:any) =>{
        patching(this.props.id,this.props.resolverr,null,null,1,null);
        this.forceUpdate();
        this.setState({
           status: 'approved'  ,  
       })
   }
    render(){
        let yes = false;
        if(this.props.who === "finance-manager" && this.props.status === "pending"){
             yes = true;
        }
        return(
            <>
            <div key={this.props.thekey || Math.floor(Math.random() * 1000)} className="col">
            <span>Reimbursement #  <span>{this.props.id}</span></span>  
            <span>author  <span>{this.props.author}</span></span>  
            <span>amount  <span>{this.props.amount}</span></span>  
            <span>description <span>{this.props.description}</span></span>  
            <span>status <span>{this.state.status}</span></span>  
            <span>type <span>{this.props.type}</span></span>  
            <span>Resolver <span>{this.props.resolver}</span></span>  
            <span>date submitted <span>{this.props.date1}</span></span> 
            <span>date resolved<span> {this.props.date2}</span></span> 
            {yes && <span><button onClick={this.approve}>Approved</button><button onClick={this.deny}>Denied</button><button onClick={this.pending}>cancel</button></span>}
            </div>
            </>
        )
    }
}
