import React from 'react';
import { User } from '../models/User';
import { getSingle } from '../api/LibraryClient';


interface IBooksDisplayState {
  single: any;
  isError: boolean;
  errorMessage: string;
}

export class SingleUser extends React.Component<any, IBooksDisplayState> {

  constructor(props: any) {
    super(props);
    this.state = {
      single: 'fetching user by id',
      isError: false,
      errorMessage: '',
    }
  }

  async componentDidMount() {
    try {
      const getit = await getSingle(this.props.userid);
      if(getit){
      this.setState({
        single: getit ,
        isError: false
      })
    }
    } catch (e) {
      // We set the error information in our state.
      // This will let us render something about the error to the end user
      this.setState({
        single: '',
        isError: true,
        errorMessage: e.message,
      })
    // }
  }
  }



  clearError = () => {
    this.setState({
      isError: false,
      errorMessage: '',
    });
  }



  render() {
    const data = this.state.single;
    const dx = (JSON.stringify(data) === '{No such user with id:20}') || null;

    if(this.state.isError){
      switch(this.state.errorMessage){
        case "Request failed with status code 402":
           return <h3>login required</h3>
        case "Request failed with status code 403":
           return  <h3>Finance manager role is required!</h3>
           case "Request failed with status code 400":
           return  <h3>Provide a numeric id only!</h3>
        default :
      return <h3>{this.state.errorMessage} </h3>
      }
    }else{
    return(
      <>

      <div key={data.id} className="col">
<span>userid:  {data.id}</span>  
<span>username:  {data.username}</span>  
<span>password:  {data.password}</span>  
<span>firstname: {data.firstname}</span>  
<span>lastname: {data.lastname}</span>  
<span>email: {data.email}</span>  
<span>role: {data.role}</span>  
</div>
<div className="dark"> <span className="a">background:</span> <br />
     {dx} </div>
</>

    )
  }

  }

}
