import React from 'react';
import { User } from '../models/User';
import { getSingle, getAllUsers } from '../api/LibraryClient';


interface IBooksDisplayState {
  single: User[] |any;
  isError: boolean;
  errorMessage: string;
}

export class SingleUser extends React.Component<any, IBooksDisplayState> {

  constructor(props: any) {
    super(props);
    this.state = {
      single: '',
      isError: false,
      errorMessage: '',
    }
  }

  async componentWillReceiveProps() {
    try {
      this.setState({
        single: await getSingle(this.props.userid)
      })
    } catch (e) {
      // We set the error information in our state.
      // This will let us render something about the error to the end user
      this.setState({
        isError: true,
        errorMessage: e.message,
      })
    }
  }



  clearError = () => {
    this.setState({
      isError: false,
      errorMessage: '',
    });
  }



  render() {

    return(
      JSON.stringify(this.state.single)
    )
