import React from 'react';
import { getPostById, updatePost, updateSelf } from '../../api/LibraryClient';
import { FormGroup, Input, Form } from 'reactstrap';
import { Post } from '../../models/Post';
import { User } from '../../models/User';


export  class Writerview extends React.Component<any,any> {
    constructor(props:any){
        super(props);
        this.state= {
          username:'',
        password:'',
        firstname1: '',
        lastname1:'',
        email: '',
        phone:'',
        permission:null,
        views:false,
        }

    }
    delete = ()=>{

    }
    view= (pw: any) => {
      this.setState({
        views:true,
      })
    }
    unview= (pw: any) => {
      this.setState({
        views: false,
      })
    }

    setUsername= (pw: any) => {
      this.setState({
        username: pw.currentTarget.value,
      })
    }
    setPassword= (pw: any) => {
      this.setState({
        password: pw.currentTarget.value,
      })
    }
    setFirstname= (pw: any) => {
      this.setState({
        firstname1: pw.currentTarget.value,
      })
    }
    setLastname= (pw: any) => {
      this.setState({
        lastname1: pw.currentTarget.value,
      })
    }
    setEmail= (pw: any) => {
      this.setState({
        email: pw.currentTarget.value,
      })
    }
    setPhone = (pw: any) => {
      this.setState({
        phone: pw.currentTarget.value,
      })
    }
    setPermission = (pw: any) => {
      this.setState({
        permission: pw.currentTarget.value,
      })
    }

    attemptUpdate = async (event: any) => {
      event.preventDefault();
      console.log(event);
      try {
        const levelup : User = await updateSelf(this.props.writerid,this.state.username,this.state.password,this.state.firstname1,
          this.state.lastname1,this.state.email,this.state.phone,this.state.userinfo.permission);
        this.setState({
          info: false,  
          userinfo: levelup,      
        });
        console.log(levelup);

      } catch (error) {
        this.setState({
          info: true,
        })
      }
    }
    render(){
      const roles = this.props.permission;
      let rolex
      switch(roles){case 1:rolex = "admin";break;case 2:rolex= "editor";break;default: rolex="writer";}
        return(

            <>
            <div className="rowss">
            <div>
            <span className="profile">{rolex}</span>
            <span>{this.props.firstname + " " + this.props.lastname}</span>
            <span>{this.props.email}</span>
            <span>{this.props.phone}</span>
            </div>
            <div>
            <span>{this.props.id}</span>
            <button onClick={this.delete}>Delete</button>
            { !this.state.views &&<button onClick={this.view}>update information</button>}
            {this.state.views &&<button onClick={this.unview}>cancel</button>}
            </div>
            { this.state.views && <div>
            <Form onSubmit={this.attemptUpdate}>
                <p>Update user's profile information</p>
        <span className="tag"> update </span>
            <FormGroup>
          <Input onChange={this.setUsername} value={this.state.username} type="text" name="username"
           id="set username" placeholder={"username:  "+ this.state.username}
           />
           </FormGroup>
           <FormGroup>
           <Input onChange={this.setPassword} value={this.state.password} type="password" name="password"
           id="password" placeholder="password:  (*******)"
           />
           </FormGroup>
           <FormGroup>
           <Input onChange={this.setFirstname} value={this.state.firstname} type="text" name="firstname"
           id="firstname" placeholder={"firstname: "+ this.state.firstname}
           />
           </FormGroup>
           <FormGroup>
           <Input onChange={this.setLastname} value={this.state.lastname} type="text" name="lastname"
           id="lastname" placeholder={ "lastname:  "+this.state.lastname}
           />
           </FormGroup>
           <FormGroup>
           <Input onChange={this.setEmail} value={this.state.email} type="text" name="email"
           id="email" placeholder={"email: "+this.state.email}
           />
           </FormGroup>
           <FormGroup>
           <Input onChange={this.setPhone} value={this.state.phone} type="text" name="phone"
           id="phone" placeholder={"phone: " +this.state.phone}
           />

          </FormGroup>
          <FormGroup>

          <select onChange={this.setPermission} name="type" required>
        <option value={this.state.permission} selected>{this.state.permission}</option>
            <option value="3">Writer</option>
            <option value="2">Editor</option>
            <option value="1">Admin</option>
            </select>
            </FormGroup>
          <FormGroup className="notdiv">
            <Input type="submit" value="submit" />
          </FormGroup>
      </Form>
            </div>}
            </div>
            </>
        )
    }
}
