import React, {useState, useContext,createContext} from 'react';
import { User } from '../models/User';
import { getSingle } from '../api/LibraryClient';
// import { QuickTable } from './QuickTable';


export const MovieContext = createContext();

export class SingleUser extends React.Component<> {

  constructor(props: any) {
    super(props);
    this.state = {
        Onemembers: '',
      isError: false,
      errorMessage: '',
      userid: ''
    }
  }

  async componentDidMount() {
    try {
      this.setState({
        Onemembers : await getSingle(this.state.userid)
      })
    } catch (e) {
      // We set the error information in our state.
      // This will let us render something about the error to the end user
      this.setState({
        isError: true,
        errorMessage: e.message,
      })
    }
  }

  updateUser = (user:any) => {
    this.setState({
      Onemembers: user,
    })
  }

  clearError = () => {
    this.setState({
      isError: false,
      errorMessage: '',
    });
  }

  render() {

    if(this.state.isError) {
        switch(this.state.errorMessage){
            case "Request failed with status code 402":
               return <h3>login required</h3>
            case "Request failed with status code 403":
               return  <h3>Finance manager role is required!</h3>
            default :
               return <h3>unknown Error</h3>
        }
    } 
else {
    const data =  this.state.Onemembers;
    return (  
    <div key={data.id} className="col">
    <span>userid:  {data.id}</span>  
    <span>username:  {data.username}</span>  
    <span>password:  {data.password}</span>  
    <span>firstname: {data.firstname}</span>  
    <span>lastname: {data.lastname}</span>  
    <span>email: {data.email}</span>  
    <span>role: {data.role}</span>  
    </div>
    )   
}

  }

}
