import React from 'react';
import { authorRe } from '../api/LibraryClient';
import { Reimbursement } from '../models/Reimbursement';


interface IBooksDisplayState {
  single: any;
  isError: boolean;
  errorMessage: string;
}

export class Authored extends React.Component<any, IBooksDisplayState> {

  constructor(props: any) {
    super(props);
    this.state = {
      single: 'fetching user by id',
      isError: false,
      errorMessage: '',
    }
  }

  async componentWillReceiveProps() {
    try {
      const getit = await authorRe(this.props.userid);
      if(getit){
      this.setState({
        single: getit ,
        isError: false
      })
    }
    } catch (e) {
      // We set the error information in our state.
      // This will let us render something about the error to the end user
      this.setState({
        single: '',
        isError: true,
        errorMessage: e.message,
      })
    // }
  }
  }



  clearError = () => {
    this.setState({
      isError: false,
      errorMessage: '',
    });
  }



  render() {

    const data = this.state.single;
    console.log(typeof(data));
    if(typeof(data) === "string"){ 
        switch(this.state.errorMessage){
            case "Request failed with status code 400":
               return <h3>Must include numeric value</h3>
            case "Request failed with status code 401":
               return  <h3>Finance manager role is required!</h3>
               case "Request failed with status code 405":
               return  <h3>No Reimbursement/s found</h3>
            default :
               return <h3>{JSON.stringify(data)}</h3>
        }

    }

    if(this.state.isError){
      switch(this.state.errorMessage){
        case "Request failed with status code 402":
           return <h3>login required</h3>
        case "Request failed with status code 403":
           return  <h3>Finance manager role is required!</h3>
           case "Request failed with status code 400":
           return  <h3>Provide a numeric id only!</h3>
        default :
      return <h3>{this.state.errorMessage} </h3>
      }
    }else{

        return (data.map((u:Reimbursement|any) =>{
            return(
     <div key={u.reimbursementId} className="col">
<span>id:  {u.reimbursementId}</span>  
<span>author:  {u.author}</span>  
<span>amount:  {u.amount}</span>  
<span>description: {u.description}</span>  
<span>status: {u.status}</span>  
<span>type: {u.type}</span>  
<span>date submitted: {u.dateSubmitted}</span> 
<span>date resolved: {u.dateResolved}</span> 
</div>

)      
})

        )
  }

  }

}
