import React from 'react';
import { User } from '../models/User';
import { getSingle } from '../api/LibraryClient';
// import { QuickTable } from './QuickTable';

interface IBooksDisplayState {
  Onemembers: User[];
  isError: boolean;
  errorMessage: string;
}

export class SingleUser extends React.Component<any, IBooksDisplayState> {

  constructor(props: any) {
    super(props);
    this.state = {
        Onemembers: [],
      isError: false,
      errorMessage: '',
    }
  }

  async componentDidMount() {
    try {
      this.setState({
        Onemembers: await getSingle()
      })
    } catch (e) {
      // We set the error information in our state.
      // This will let us render something about the error to the end user
      this.setState({

        isError: true,
        errorMessage: e.message,
      })
    }
  }



  clearError = () => {
    this.setState({
      isError: false,
      errorMessage: '',
    });
  }

  render() {

    if(!this.state.isError) {
      //map to a string so quicktable can render this.

      return this.state.Onemembers;
    } 
else {

return  <h3>{this.state.errorMessage}</h3>

    }

  }

}
